import Foundation

let sample = "bvwbjplbgvbhsrlpgdmjqwftvncz"
let sample2 = "nppdvjthqldpwncqszvftbrmjlhg"
let sample3 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"
let sample4 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"
let input = """

"""
func findUniqueSequence(_ input: String) -> Int {
    let chars = Array(input)
    for (index, _) in chars.enumerated() {
        if index < 3 {
            continue
        }
        if Set(chars[(index - 3)...index]).count == 4 {
            return index + 1
        }
    }
    return -1
}

print(findUniqueSequence(sample))
print(findUniqueSequence(sample2))
print(findUniqueSequence(sample3))
print(findUniqueSequence(sample4))
print(findUniqueSequence(input))
