import Foundation

let sample = "bvwbjplbgvbhsrlpgdmjqwftvncz"
let sample2 = "nppdvjthqldpwncqszvftbrmjlhg"
let sample3 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"
let sample4 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"
let input = """

"""
func findUniqueSequence(_ input: String, length: Int) -> Int {
    let chars = Array(input)
    for (index, _) in chars.enumerated() {
        if index < length - 1 {
            continue
        }
        if Set(chars[(index - (length - 1))...index]).count == length {
            return index + 1
        }
    }
    return -1
}

let length = 14

print(findUniqueSequence(sample, length: length))
print(findUniqueSequence(sample2, length: length))
print(findUniqueSequence(sample3, length: length))
print(findUniqueSequence(sample4, length: length))
print(findUniqueSequence(input, length: length))
